const TIME_TO_FULLY_OPEN = 28000;  // in milli seconds
const TIME_TO_PAUSE = 3000;        // in milli seconds
const EPSILON = 3                  // ignore small position changes below this number of degrees
const press_open =  [ { payload: 1 }, null, null ];
const press_stop =  [ null, { payload: 1 }, null ];
const press_close = [ null, null, { payload: 1 } ];
const press_null =  [ null, null, null ];

let position = context.get('position');
let actions = [];
let delay_1 = 0

// check if we need to reset the shutter
if (position === undefined) {
    actions.push({ delay: 0, msgs: press_close, fill: 'yellow', text: 'Resetting' });
    delay_1 = TIME_TO_FULLY_OPEN+TIME_TO_PAUSE;
    position = 0;
}

// clamp the command
let cmd = ( msg.payload < 0 + EPSILON ) ? 0 :
          ( msg.payload > 100 - EPSILON ) ? 100 :
            msg.payload;

// when no significant change
if (Math.abs(position - cmd) < EPSILON) {
    actions.push({ delay: delay_1, msgs: press_null, fill: 'yellow', text: `No Change for ${cmd.toFixed(1)}%` });
    cmd = position;
    actions.push({ delay: delay_1+TIME_TO_PAUSE, msgs: press_null, fill: 'green', text: `Ready at ${cmd.toFixed(1)}%` });

// when need to open shutter wider
} else if (cmd > position) {
    const delay_2 = TIME_TO_FULLY_OPEN * Math.abs(cmd - position) / 100;
    const maybe_press_stop = (cmd == 100) ? press_null : press_stop;
    actions.push({ delay: delay_1, msgs: press_open, fill: 'yellow', text: `Opening for ${(delay_2/1000).toFixed(1)}s` });
    actions.push({ delay: delay_1+delay_2, msgs: maybe_press_stop, fill: 'green', text: `Ready at ${cmd.toFixed(1)}%` });

// when need to close shutter narrower
} else {
    const delay_2 = TIME_TO_FULLY_OPEN * Math.abs(cmd - position) / 100;
    const maybe_press_stop = (cmd === 0) ? press_null : press_stop;
    actions.push({ delay: delay_1, msgs: press_close, fill: 'yellow', text: `Closing for ${(delay_2/1000).toFixed(1)}s` });
    actions.push({ delay: delay_1+delay_2, msgs: maybe_press_stop, fill: 'green', text: `Ready at ${cmd.toFixed(1)}%` });
}

context.set('position', cmd);

// schedule all the actions
actions.forEach(action => {
    setTimeout(function() {
        node.status({fill: action.fill, shape: 'dot', text: action.text});
        node.send( action.msgs );
    }, action.delay);

})

return;

// 86 Excelsior Rd is aligned 10° to the east of True North 
const orientation = ( 10 ) * (2 * Math.PI) / 360;               // orientation of blind axis 0 = North, 90 = East, etc.
const h = Math.sin(msg.payload.altitudeRadians);                // unit height of sun vertically
const z = Math.cos(msg.payload.altitudeRadians);                // unit distance to sun horizontally
const w = z * Math.sin(msg.payload.azimuthRadians-orientation); // unit distance to sun horizontally perpendicular to blind axis
const theta = Math.atan2(h, w)*360/(2*Math.PI);                 // blind angle to sun 0 = sunset, 180 = sunrise
msg.payload.blind_angle = theta;

const status_text = `z ${z.toFixed(1)} | h ${h.toFixed(1)} | w ${w.toFixed(1)} | Theta ${theta.toFixed(1)}°`;
node.status({fill:"green", shape:"dot", text: status_text });

const log_text = `Elev ${msg.payload.altitude.toFixed(1)} | Azim ${msg.payload.azimuth.toFixed(1)} | ${status_text}`
node.log(log_text);

return msg;

// Convert blind_angle (0=sunset, 180=sunrise) to shutter position (0=closed, 100=open)
//
//     shutter position
//            |
//            |
//        100 |   +
//            |   *   *
//            |   *       *
//         60 *****           +               ****
//         50 *                   *           *  *
//            *                       *       *  *
//            *                           *   *  *
//            *                               +  *
//  **********+----------------------------------+*********>  blind_angle
//            0   15          90             165 180°
//          sunset                               sunrise
//
const ANGLE_AT_100PC = 30;                      // equiv to 90° at 60%
const ANGLE_REFLECTION = -95;                  // angle to point reflection at -90 is vertically down
const ba = msg.payload.blind_angle;
const m = -100/(180 - ANGLE_AT_100PC);         // line slope
const c = 100 - m * ANGLE_AT_100PC;            // line intercept

msg.payload = (ba < -180) ? 0 :                 // invalid ie closed
              (ba > 180) ? 0 :                  // invalid ie closed
              (ba < -175) ? 60 :                // =180 to -175 sunrise ie open wide
              (ba < 0) ? 0 :                    // -175 to 0 ie sun below horiz ie closed
              (ba < ANGLE_AT_100PC) ? 60 :      // 0 to 15 very late afternoon  ie open wide
              (ba > 165) ? 60 :                 // 165 to 180 very early morning ie open wide
              (ba > 100) ? m * ((ba+ANGLE_REFLECTION)/2+90) + c :  // track the reflection  
              m * ba + c;                       // track the sun ie y = mx + c

const text = `Theta ${ba.toFixed(1)}° | Pos ${msg.payload.toFixed(1)}%`;
node.status({fill:"green", shape:"dot", text });
node.log(text);

return msg;

const auto_weather = [ null, msg, null ];
const auto_force = [msg, null, null];
const manual_action = x => [null, null, { payload: x }];
const no_action = [ null, null, null ]

let mode = context.get('mode') || 'auto';
let sun = context.get('sun') || 'is up';
let msgs = no_action;

switch(msg.payload) {
    case 'auto':
        msgs = auto_weather;
        mode = 'auto';
        break;

    case 'auto_force':
        msgs = auto_force;
        mode = 'auto';
        break;

    case 'up':
        msgs = (mode == 'auto') ? auto_weather : no_action;
        sun = 'is up';
        break;

    case 'down':
        msgs = manual_action(0);
        sun='is down';
        break;

    case 'tick':
        if (mode == 'auto' && sun == 'is up')
            msgs = auto_weather;
        break;

    default:
        msgs = manual_action(msg.payload);
        mode = 'manual';
        break;

}

const text = `${mode} Mode | Sun ${sun}`
node.status({fill:"green", shape:"dot", text});

context.set('mode', mode);
context.set('sun', sun);

return msgs;

const close_shutter = [ null, { payload: 0 } ];
const auto_shutter = [ msg, null ];
const format_result = w => `Temp ${w.temperature}°C | Cloud ${(w.cloudCover*100).toFixed(0)}% | Rain ${w.precipIntensity.toFixed(2)} mm/hr | Prob ${(w.precipProbability*100).toFixed(0)}%`;
const format_time = d => new Date(d*1000).toString().split('(')[0];

let output_msgs = auto_shutter;
let w = {};

w = msg.payload.currently;
node.log(`${format_time(w.time)}- ${format_result(w)}`);
if (w.precipProbability>0.03) {
    node.status({fill: 'yellow', shape: 'dot', text: format_result(w)});
    output_msgs = close_shutter;
} else {
    node.status({fill: 'green', shape: 'dot', text: format_result(w)});
}


w = msg.payload.hourly.data[0];
node.log(`${format_time(w.time)}- ${format_result(w)}`);

w = msg.payload.hourly.data[1];
node.log(`${format_time(w.time)}- ${format_result(w)}`);


return output_msgs;

const code = String(msg.payload)
const mapping = {
    '398': 0,
    '399': 'auto',
    '400': 'auto',
    '401': 'auto_force',
    '512': 0,
    '513': 10,
    '514': 20,
    '515': 30,
    '516': 40,
    '517': 50,
    '518': 60,
    '519': 70,
    '520': 80,
    '521': 90,
}

if (mapping[code] !== undefined) {
    msg.payload = mapping[code];
    node.status({ fill:"green", shape:"dot", text: `Cmd ${msg.payload}`});
} else {
    msg = null;
    node.status({fill:"yellow", shape:"dot", text: `Code ${code}`});
}

return msg;
